import React from 'react'

import s from './Component.css'

class Component extends React.Component {
    constructor(props){
        super(props)
    }
    render() {
        return (
            <Demo {...props}/>
        )
    }
}



const Container = (props) => <div className="container">{props.children}</div>;

const Switch = (props) => (
  <label className="switch">
    <input type="checkbox" checked={props.checked} onChange={(e)=>
        props.onChange(e.target.checked)
    }/>
    <div className={`slider ${props.round ? "round" : ""}`} />
  </label>
);

const FilterElement = ({ children, ...rest }) => (
  <div className="filter element">
    <div className="toggle">
      <Switch {...rest} />
    </div>
    <div className="content">{children}</div>
  </div>
);

const Demo = (props) => (
    <FilterElement onChange={props.onChange} round>
        <span>{props.text}</span>
    </FilterElement>
);



export default Component